  closeOnPointerDown?: MaybeGetter<boolean | undefined>;

  /**
   * Tooltip open delay in milliseconds.
   *
   * @default 1000
   */
  openDelay?: MaybeGetter<number | undefined>;

  /**
   * Tooltip close delay in milliseconds.
   *
   * @default 0
